import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function Project(props) {
  const styles = {
      display: 'flex',
      flexDirection: 'column',
      marginLeft: '40px',
      marginBottom: '50px'
  }

    return (
      <>
        <h1>Projects</h1>
          {props.challenges.map((challenge, index) => (
            <div style={styles}>
              <h3 key={index}>{challenge.title}</h3>
              <img key={index} src={challenge.image}></img>
              <a key={index} href={challenge.deployedURL}>Visit App</a>
              <a key={index} href={challenge.repo}>View GitHub Repo</a>
            </div>
          ))}

          {props.challenges.map((challenge, index) => (
            <Card style={{ width: '18rem' }}>{challenge.image} />
            <Card.Body>
              <Card.Title key={index}>{challenge.title}</Card.Title>
              <Card.Text>
                Some quick example text to build on the card title and make up the
                bulk of the card's content.
              </Card.Text>
              <Button variant="primary" key={index} href={challenge.deployedURL}>Visit App</Button>
              <Button variant="primary" key={index} href={challenge.repo}>View GitHub Repo</Button>
            </Card.Body>
          </Card>
          ))}
      </>
    )
  }

  export default Project;
